glsl3.vert:
  precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
  in vec3 a_position;
  in vec4 a_color;
  out vec4 v_color;
  in vec2 a_uv0;
  out vec2 v_uv0;
  void main () {
      vec4 pos = vec4(a_position, 1);
      #if CC_USE_MODEL
          pos = cc_matViewProj * cc_matWorld * pos;
      #else
          pos = cc_matViewProj * pos;
      #endif
      v_color = a_color;
      v_uv0 = a_uv0;
      gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
in vec2 v_uv0;
in vec4 v_color;
const lowp float EPSILON = 0.0001;
const lowp float GLEAM_WIDTH = 0.1;
const lowp float GLEAM_WEIGHT = 1. + 2. * GLEAM_WIDTH;
uniform PROPERTIES {
  float visibility;
  float time;
  float smoothness;
  float taper;
};
uniform sampler2D texture;
void main() {
  #if useTexture
    vec4 o = texture2D(texture, v_uv0);
  #else
    vec4 o = v_color;
  #endif
  #if isReversed
    float x = 1. - v_uv0.x;
  #else
    float x = v_uv0.x;
  #endif
  float t = time * GLEAM_WEIGHT - GLEAM_WIDTH;
  float v = visibility * GLEAM_WEIGHT - GLEAM_WIDTH;
  o.rgb += (1. - smoothstep(0., GLEAM_WIDTH, abs(t - x)));
  #if usePremultiplyAlpha
    o *= step(max(EPSILON, x), v);
  #else
    o.a *= step(max(EPSILON, x), v);
  #endif
  #if (useSmoothing || useTaper)
    float y = abs(v_uv0.y - 0.5);
    #if useSmoothing
      #if usePremultiplyAlpha
        o *= 1. - smoothstep(smoothness, 1., y);
      #else
        o.a *= 1. - smoothstep(smoothness, 1., y);
      #endif
    #endif
    #if useTaper
      x = 1. - abs(x - 0.5) * 2.;
      #if usePremultiplyAlpha
        o.a *= smoothstep(0., taper, x * (1. - y));
      #else
        o *= smoothstep(0., taper, x * (1. - y));
      #endif
    #endif
  #endif
  gl_FragColor = o;
}

